export default function ProjectPresentation({
  projectName,
  title,
  description,
  mapImage,
  timings = [],
  features = [],
}) {
  return (
    <section className="mx-auto max-w-7xl px-6 py-20">
      <h2 className="mb-8 text-center text-6xl font-semibold font-cursive">
        {projectName}
      </h2>

      <h3 className="mb-6 text-3xl font-bold text-gray-900">
        Pr√©sentation du projet
      </h3>

      <div className="grid gap-8 rounded-3xl bg-[#F2F3FF] p-8 md:grid-cols-2">
        {/* Left */}
        <div>
          <h4 className="mb-4 text-2xl font-semibold text-gray-900">
            {title}
          </h4>

          <p className="leading-relaxed text-gray-700">
            {description}
          </p>

          {/* Timings */}
          <div className="mt-6 flex flex-wrap gap-6">
            {timings.map((timing, index) => {
              const Icon = TIMING_ICONS[timing.type];

              return (
                <div
                  key={index}
                  className="flex items-center gap-2 text-sm text-gray-700"
                >
                  <span className="flex h-8 w-8 items-center justify-center rounded-full bg-blue-100 ">
                    {Icon ? Icon : null}
                  </span>
                  <span>{timing.minutes} min</span>
                </div>
              );
            })}
          </div>

          {/* Features */}
          <div className="mt-8 flex flex-wrap gap-10">
            {features.map((feature, index) => {
              const Icon = FEATURE_ICONS[feature.key];

              return (
                <div
                  key={index}
                  className="flex flex-col items-center gap-2 text-center"
                >
                  <div className="flex h-14 w-14 items-center justify-center rounded-full bg-primary text-white">
                    {Icon ? Icon : null}
                  </div>
                  <span className="text-sm font-medium text-gray-800">
                    {feature.value} {feature.label}
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Right */}
        <div className="overflow-hidden rounded-2xl bg-white shadow">
          <img
            src={mapImage}
            alt="Localisation du projet"
            className="h-full w-full object-cover"
          />
        </div>
      </div>
    </section>
  );
}
